const initState = {
    todo: [],
    post: [
        {id:'1',title:'task redux',data:'ini data redux1'},
        {id:'1',title:'task redux',data:'ini data redux1'},
        {id:'1',title:'task redux',data:'ini data redux1'}
    ]
};

const myReducer = (state = initState, action) => {
    if (action.type === 'ADD_TODO') {
        return {
            ...state,
            todo: [...state.todo, action.todo]
        }
    }
    if (action.type === 'ADD_POST') {
        return {
            ...state,
            post: [...state.post, action.post]
        }
    }
}

